	(157, 'aerologi', 'ae&#183;ro&#183;lo&#183;gi /a&#233;rologi/ n 1 meteorologi; 2 cabang meteorologi yg berhubungan dng udara; 3 Geo ilmu mengenai atmosfer; 4 Fis ilmu tt udara, khususnya udara yg ada di atas'),
	(158, 'aerometer', 'ae&#183;ro&#183;me&#183;ter /a&#233;rom&#233;ter/ n alat untuk mengukur bobot atau kerapatan udara atau gas lain'),
	(159, 'aeromovel', 'ae&#183;ro&#183;mo&#183;vel /a&#233;romov&#233;l/ n kereta api yg dijalankan dng tenaga magnet di bagian bawahnya'),
	(160, 'aeronautika', 'ae&#183;ro&#183;nau&#183;ti&#183;ka /a&#233;ronautika/ n ilmu penerbangan'),
	(161, 'aeroplankton', 'ae&#183;ro&#183;plank&#183;ton /a&#233;roplankton/ n Bio makhluk, plankton, zarah hidup spt spora, bakteri, dan serbuk sari yg melayang di udara dan dipencarkan oleh angin'),
	(162, 'aeroskop', 'ae&#183;ro&#183;skop /a&#233;roskop/ n alat untuk menangkap debu, bakteri, spora, dsb dr udara untuk tujuan tes (percobaan, pengujian)'),
	(163, 'aerosol', 'ae&#183;ro&#183;sol /a&#233;rosol/ n Kim sistem tersebarnya partikel halus zat padat atau cairan dl gas atau udara, msl asap dan kabut'),
	(164, 'aerostat', 'ae&#183;ros&#183;tat /a&#233;rostat/ n Fis pesawat yg lebih ringan dp udara'),
