	(10685, 'istihsan', 'is&#183;tih&#183;san n Isl pendapat yg berpegang pd kebaikan sesuatu bagi umat manusia sehingga apa yg dipandang baik boleh dikerjakan atau dipedomani'),
	(10686, 'istikamah', 'is&#183;ti&#183;ka&#183;mah n Isl sikap teguh pendirian dan selalu konsekuen: Alquran mengajarkan kita agar mempunyai sikap --'),
	(10687, 'istikharah', 'is&#183;ti&#183;kha&#183;rah n Isl salat sunah mohon ditunjukkan pilihan yg benar;ber&#183;is&#183;ti&#183;kha&#183;rah v Isl bersalat sunah memohon kpd Allah supaya ditunjukkan pilihan yg benar'),
